		[E0100201_unbegrenzt] := [NamensVorsatz] + " " + [Name]
	}
	Wenn(FeldNichtAngegeben(NamensVorsatz) Und FeldAngegeben(NamensZusatz)) {
		[E0100201_unbegrenzt] := [Name] + " " + [NamensZusatz]
	}
}

