	Wenn(AlleFelderAngegeben(NamensVorsatz, NamensZusatz)) {
		[E0100901_unbegrenzt] := [NamensVorsatz] + " " + [Name] + " " + [NamensZusatz]
	}
	Wenn(KeinFeldAngegeben(NamensVorsatz, NamensZusatz)) {
		[E0100901_unbegrenzt] := [Name]
	}
	Wenn(FeldAngegeben(NamensVorsatz) Und FeldNichtAngegeben(NamensZusatz)) {
		[E0100901_unbegrenzt] := [NamensVorsatz] + " " + [Name]
	}
	Wenn(FeldNichtAngegeben(NamensVorsatz) Und FeldAngegeben(NamensZusatz)) {
		[E0100901_unbegrenzt] := [Name] + " " + [NamensZusatz]
	}
}

